// Get the current UTC date and time
const nowUTC = new Date();

// Extract hour and minute in UTC
const currentUTCHour = nowUTC.getUTCHours();   // 0–23
const currentUTCMinute = nowUTC.getUTCMinutes(); // 0–59

// Also include a timestamp for easier debugging/logging
const isoTimestamp = nowUTC.toISOString();

// Return data for the next node
return [{
  json: {
    currentUTCHour,
    currentUTCMinute,
    isoTimestamp, // optional but useful for logs or DB entries
    note: "Current UTC time details for timezone-based 8AM newsletter"
  }
}];

// Get current UTC time data from previous node
const input = $input.first().json;
const currentUTCHour = input.currentUTCHour;
const currentUTCMinute = input.currentUTCMinute || 0;

// Timezone offset map
const timezoneOffsets = {
  'Pacific/Midway': -11,
  'Pacific/Honolulu': -10,
  'America/Anchorage': -9,
  'America/Los_Angeles': -8,
  'America/Denver': -7,
  'America/Chicago': -6,
  'America/New_York': -5,
  'America/Caracas': -4,
  'America/Sao_Paulo': -3,
  'Atlantic/South_Georgia': -2,
  'Atlantic/Azores': -1,
  'UTC': 0,
  'Europe/London': 0,
  'Europe/Paris': 1,
  'Europe/Berlin': 1,
  'Europe/Athens': 2,
  'Europe/Moscow': 3,
  'Asia/Dubai': 4,
  'Asia/Karachi': 5,
  'Asia/Kolkata': 5.5,
  'Asia/Dhaka': 6,
  'Asia/Bangkok': 7,
  'Asia/Singapore': 8,
  'Asia/Shanghai': 8,
  'Asia/Tokyo': 9,
  'Australia/Sydney': 10,
  'Australia/Brisbane': 10,
  'Pacific/Noumea': 11,
  'Pacific/Fiji': 12
};

const targetTimezones = [];

// Convert UTC hour + minutes into decimal
const utcDecimal = currentUTCHour + currentUTCMinute / 60;

// Loop through all timezones
for (const [tz, offset] of Object.entries(timezoneOffsets)) {
  let localTime = utcDecimal + offset;

  // Wraparound for 0–24
  if (localTime < 0) localTime += 24;
  if (localTime >= 24) localTime -= 24;

  // Check if timezone is about to hit 8 AM (7:00–7:59)
  if (localTime >= 7 && localTime < 8) {
    targetTimezones.push({
      timezone: tz,
      localHour: Math.floor(localTime),
      localMinute: Math.round((localTime % 1) * 60)
    });
  }
}

// Create flat array of timezone strings for Supabase
// Keep original casing to match DB exactly
const timezoneNames = targetTimezones.map(t => t.timezone);

// Return JSON for next nodes
return [{
  json: {
    currentUTCHour,
    currentUTCMinute,
    targetTimezones,   // full objects (optional)
    timezoneNames,     // flat array for Supabase query
    timezoneCount: targetTimezones.length
  }
}];

return [{
  json: {
    message: "No subscribers found for this hour. Workflow complete.",
    hour: new Date().getUTCHours(),
    timestamp: new Date().toISOString()
  }
}];
